import React from "react";
import { useNavigate } from "react-router-dom";
import './Home.css';
import logo from './logo.png';
import Web3 from "web3";
import configuration from '../../build/contracts/ART.json';

const CONTRACT_ADDRESS = configuration.networks['5777'].address;
const CONTRACT_ABI = configuration.abi;


const web3 = new Web3(
  Web3.givenProvider || 'http://127.0.0:7545'
);

const contact = new web3.eth.Contract(CONTRACT_ABI, CONTRACT_ADDRESS);

let account;

const accountEl = document.getElementById('account');

const main = async () => {
  const accounts = await web3.eth.requestAccounts();
account = account[0];
}

main ();


const Home = (props) => {
  const navigate = useNavigate();

  return (
    <div className="container">
      {/* Image */}
      <img src={logo} alt="logo" className="logo" />

    <div id="account"></div>

      {/* Buttons */}
      <div className="button-container">
        <button className="button" onClick={() => navigate("/services")}>
          Mint Token
        </button>
        <button className="button" onClick={() => navigate("/sell")}>
          Sell an Item
        </button>
        <button className="button" onClick={() => navigate("/about")}>
          List all items for sale
        </button>
        <button className="button" onClick={() => navigate("/blog")}>
          Change price of an item
        </button>
      </div>
    </div>
  );
};

export default Home;
