import React from 'react';
import './App.css';
import logo from './logo.png';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';


function Home() {
  return (
    <div className="container">
      <img src={logo} alt="Logo" style={{ width: '300px', height: '90px', marginBottom: '80px', marginTop: '30px' }} />
      <Link to="/sell"><button className="button">Sell an Item</button></Link>
      <button className="button">Mint Token</button>
      <button className="button">List all items for sale</button>
      <button className="button">Change price of an item</button>
    </div>
  );
}

function SellItem() {
  return (
    <div className="container">
      <h1>Sell an Item</h1>
      <form>
        <label>
          Item Name:
          <input type="text" name="name" />
        </label>
        <br />
        <label>
          Item Price:
          <input type="text" name="price" />
        </label>
        <br />
        <label>
          Item Description:
          <input type="text" name="description" />
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Switch>
        <Route exact path="/" component={Home} />
        <Route path="/sell" component={SellItem} />
      </Switch>
    </Router>
  );
}

export default App;
